package fr.diginamic.jdr;

public class Loup {

	private int force;
	private int pdv;

	public Loup(int force, int pdv) {
		// TODO Auto-generated constructor stub
		this.force = force;
		this.pdv = pdv;
	}

	public int getForce() {
		return force;
	}

	public void setForce(int force) {
		this.force = force;
	}

	public int getPdv() {
		return pdv;
	}

	public void setPdv(int pdv) {
		this.pdv = pdv;
	}

}
